#include <bits/stdc++.h>
using namespace std;

// https://adventofcode.com/2015/day/1

int find_floor(string input)
{


	int current_floor = 0;

	for (int i = 0; i < input.length(); ++i)
	{
		char e = input[i];
		if (e == '(')
        {
			current_floor++;
        }

        if (e == ')')
        {
			current_floor--;
        }
		
		if (current_floor == -1) // find the position of the first character that causes him to enter the basement (floor -1).
		{
			return (i+1);
		}
    }
    
    return current_floor;
}

